package com.express.service.mapping;import org.dozer.CustomConverter;/** * @author Adam Boas * */public class IdConverter implements CustomConverter{   /**     * Enforces that when an id of zero passed in it is mapped as null instead.    *     */   @SuppressWarnings("unchecked")   public Object convert(Object existingDestinationFieldValue, Object sourceFieldValue,            Class destinationClass, Class sourceClass)   {      Long longValue = (Long)sourceFieldValue;      if(longValue == null || longValue == 0) {         return null;      }      else {         return longValue;      }   }}